module add1 (
    input wire a,
    input wire b,
    input wire Cin,
    output wire y,
    output wire Co
);
    assign y = ((~a) & (~b) & Cin | (~a) & b & Cin | a & (~b) & (~Cin) | (a & b & Cin)); 
    assign Co = ((~a & b & Cin) | (a & ~b & Cin) | (a & b & ~Cin) | (a & b & Cin));

    always @(*)begin
       assert ((a + b + Cin) == {Co, y})
       else $error("adder_16bit sum is incorrect!");

 end

    always @* begin
        assert (y == ((~a) & (~b) & Cin | (~a) & b & (~Cin) | a & (~b) & (~Cin) | (a & b & Cin)))
        else $fatal("Assertion failed in add1: y is not correct");
        assert (Co == ((~a & b & Cin) | (a & ~b & Cin) | (a & b & ~Cin) | (a & b & Cin)))
        else $fatal("Assertion failed in add1: Co is not correct");
    end 

endmodule

