    @(posedge clk) disable iff (reset)
    (ctr < 16) |=> (multiplicand == $past(multiplicand) << 1);
  endproperty
  multiplicand_shift_assert: assert property (multiplicand_shift);


  property product_accumulation;
    @(posedge clk) disable iff (reset)
    (ctr < 16 && multiplier[ctr] == 1) |=> (p == $past(p) + $past(multiplicand));
  endproperty
  product_accumulation_assert: assert property (product_accumulation);

  property rdy_stability;
    @(posedge clk) disable iff (reset)
    rdy |=> $stable(rdy);
  endproperty
  rdy_stability_assert: assert property (rdy_stability);


endmodule

