module signal_generator(
  input clk,
  input rst_n,
  output reg [4:0] wave
);

  reg [1:0] state;

  always @(posedge clk or negedge rst_n) begin
    if (~rst_n) begin
      state <= 2'b0;
      wave <= 5'b0;
    end
    else begin
      case (state)
        2'b00:
          begin
            if (wave == 5'b11111)
              state <= 2'b01;
            else
              wave <= wave ;
          end

        2'b01:
          begin
            if (wave == 5'b00000)
              state <= 2'b00;
            else
              wave <= wave - 1;
          end
      endcase
    end
  end

property max_value_check;
 @(posedge clk) 
 disable iff (~rst_n)
 wave <= 5'b11111;
endproperty
max_value_check_assert: assert property (max_value_check);


property min_value_check;
 @(posedge clk) 
 disable iff (~rst_n)
 wave >= 5'b00000;
endproperty
min_value_check_assert: assert property (min_value_check);


property wave_max_decrease;
    @(posedge clk) 
 disable iff (~rst_n)
    wave == 5'b11111 |-> ##2 wave == $past(wave) - 1;
endproperty
wave_max_decrease_assert: assert property(wave_max_decrease);


property wave_min_increase;
    @(posedge clk) 
 disable iff (~rst_n)
    wave == 5'b00000 |-> ##2 wave == $past(wave) + 1;
endproperty
