module traffic_light
    (
  input rst_n, 
      input clk, 
      input pass_request,
    output wire[7:0]clock,
      output reg red,
    output reg yellow,
    output reg green
    );

 parameter  idle = 2'd0,
    s1_red = 2'd1,
    s2_yellow = 2'd2,
    s3_green = 2'd3;
 reg [7:0] cnt;
 reg [1:0] state;
 reg p_red,p_yellow,p_green; 


always @(posedge clk or negedge rst_n) 
    begin
        if(!rst_n)
        begin
   state <= idle;
   p_red <= 1'b0;
   p_green <= 1'b0;
   p_yellow <= 1'b0;   
        end
        else case(state)
  idle:
   begin
    p_red <= 1'b0;
    p_green <= 1'b0;
    p_yellow <= 1'b0;
    state <= s1_red;
   end
  s1_red:
   begin
    p_red <= 1'b1;
    p_green <= 1'b0;
    p_yellow <= 1'b0;
    if (cnt == 3) 
    //if (cnt == 0) 
     state <= s3_green;
    else
     state <= s1_red;
   end
  s2_yellow:
   begin
    p_red <= 1'b0;
    p_green <= 1'b0;
    p_yellow <= 1'b1;
    if (cnt == 3) 
     state <= s1_red;
    else
     state <= s2_yellow;
   end
  s3_green:
   begin
    p_red <= 1'b0;
    p_green <= 1'b1;
    p_yellow <= 1'b0;
    if (cnt == 3) 
     state <= s2_yellow;
    else
     state <= s3_green;
   end
  endcase
 end

always @(posedge clk or negedge rst_n) 
      if(!rst_n)
   cnt <= 7'd10;
  else if (pass_request&&green&&(cnt<10))
   cnt <= 7'd10;
  else if (!green&&p_green)
   cnt <= 7'd60;
  else if (!yellow&&p_yellow)
   cnt <= 7'd5;
  else if (!red&&p_red)
   cnt <= 7'd10; 
  else cnt <= cnt -1;
 assign clock = cnt;

always @(posedge clk or negedge rst_n) 
        if(!rst_n)
   begin
    yellow <= 1'd0;
    red <= 1'd0;
    green <= 1'd0;
   end
  else 
   begin
    yellow <= p_yellow;
    red <= p_red;
    green <= p_green;
   end  


property red_light_duration;
    @(posedge clk) 
 disable iff (!rst_n)
    (state == s1_red) |-> (##[1:10] (state != s1_red));
endproperty
red_light_duration_assert: assert property (red_light_duration) else $error("Red light duration exceeded 10 clock cycles");


property yellow_light_duration;
    @(posedge clk) 
 disable iff (!rst_n)
    (state == s2_yellow) |-> (##[1:5] (state != s2_yellow));
endproperty
yellow_light_duration_assert: assert property (yellow_light_duration) else $error("Yellow light duration exceeded 5 clock cycles");


property green_light_duration;
    @(posedge clk) 
 disable iff (!rst_n)
    (state == s3_green) |-> (##[1:60] (state != s3_green));
endproperty
green_light_duration_assert: assert property (green_light_duration) else $error("Green light duration exceeded 60 clock cycles");


property pass_request_shortens_green;
    @(posedge clk) 
 disable iff (!rst_n)
    (pass_request && green && (cnt > 10)) |=> (cnt == 10);
endproperty
pass_request_shortens_green_assert: assert property (pass_request_shortens_green) else $error("Pass request did not shorten green light duration to 10 cycles when cnt > 10");


endmodule

`timescale 1ns/1ns
module width_8to16(
 input        clk   ,   
 input        rst_n  ,
 input          valid_in ,
 input    [7:0]     data_in ,

  output reg   valid_out,
 output   reg [15:0] data_out
);
reg  [7:0]  data_lock;  //data buffer
reg     flag    ;
//input data buff in data_lock
always @(posedge clk or negedge rst_n ) begin
 if(!rst_n) 
  data_lock <= 'd0;
 else if(valid_in && !flag)
  data_lock <= data_in + 1;
end
//generate flag
always @(posedge clk or negedge rst_n ) begin
 if(!rst_n) 
  flag <= 'd0;
 else if(valid_in)
  flag <= ~flag;
end
//generate valid_out
always @(posedge clk or negedge rst_n ) begin
 if(!rst_n) 
  valid_out <= 'd0;
 else if(valid_in && flag)
  valid_out <= 1'd1;
 else
  valid_out <= 'd0;
end
//data stitching 
always @(posedge clk or negedge rst_n ) begin
 if(!rst_n) 
  data_out <= 'd0;
 else if(valid_in && flag)
  data_out <= {data_lock, data_in};
end


property valid_out_delay;
    @(posedge clk) 
 disable iff (!rst_n)
    (valid_in && flag) |=> valid_out;
endproperty
valid_out_delay_assert: assert property (valid_out_delay);


property valid_out_inactive;
    @(posedge clk) 
 disable iff (!rst_n)
    !(valid_in && flag) |=> !valid_out;
endproperty
valid_out_inactive_assert: assert property (valid_out_inactive);


property data_out_update;
    @(posedge clk) 
 disable iff (!rst_n)
    (valid_in && flag) |=> (data_out == {$past(data_lock), $past(data_in)});
endproperty
data_out_update_assert: assert property (data_out_update);


property flag_toggle;
    @(posedge clk) 
 disable iff (!rst_n)
    valid_in |=> flag != $past(flag);
endproperty
flag_toggle_assert: assert property (flag_toggle);


property data_lock_update;
    @(posedge clk) 
 disable iff (!rst_n)
    (valid_in && !flag) |=> (data_lock == $past(data_in));
endproperty
data_lock_update_assert: assert property (data_lock_update);


property no_premature_output;
    @(posedge clk) 
 disable iff (!rst_n)
    (valid_in && !flag) |=> !valid_out;
endproperty
no_premature_output_assert: assert property (no_premature_output);


property data_stability;
    @(posedge clk) 
 disable iff (!rst_n)
    (!valid_in) |=> (data_out == $past(data_out));
endproperty
data_stability_assert: assert property (data_stability);


endmodule

